document.addEventListener('DOMContentLoaded', function () {
    const ongoingOrders = [
        {
            id: 1,
            name: 'Onion',
            price: '₹100.00',
            weight: '1 Kg',
            retailer: 'Retailer A',
            contact: 'contactA@example.com',
            phone: '123-456-7890',
            address: '123 Main St, City, Country',
            duration: '2 days',
            image: 'data:image/jpeg;base64,'
        },
        {
            id: 2,
            name: 'Potato',
            price: '₹200.00',
            weight: '1 Kg',
            retailer: 'Retailer B',
            contact: 'contactB@example.com',
            phone: '987-654-3210',
            address: '456 Another St, City, Country',
            duration: '3 days',
            image: 'data:image/jpeg;base64,'
        },
        {
            id: 5,
            name: 'pumpkin',
            price: '₹200.00',
            weight: '1 Kg',
            retailer: 'Retailer E',
            contact: 'contactE@example.com',
            phone: '988-657-3109',
            address: '456 Another St, City, Country',
            duration: '3 days',
            image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSL3GidUVX2dg12mH-QSStcOXzSIZZTb3dMgg&s'
        }
    ];

    const previousOrders = [
        {
            id: 3,
            name: 'Tomato',
            price: '₹150.00',
            weight: '1 Kg',
            retailer: 'Retailer C',
            contact: 'contactC@example.com',
            phone: '654-321-0987',
            address: '789 Elsewhere St, City, Country',
            duration: '1 day',
            image: 'data:image/jpeg;base64,'
        },
        {
            id: 4,
            name: 'Brinjal',
            price: '₹250.00',
            weight: '1 Kg',
            retailer: 'Retailer D',
            contact: 'contactD@example.com',
            phone: '321-654-9870',
            address: '101 Somewhere St, City, Country',
            duration: '5 days',
            image: 'data:image/jpeg;base64,'
        }
    ];

    const createOrderBlock = (order) => {
        const orderBlock = document.createElement('div');
        orderBlock.className = 'order-block';
        
        const orderImage = document.createElement('img');
        orderImage.className = 'order-image';
        orderImage.src = order.image;
        orderImage.alt = order.name;
        
        const orderDetails = document.createElement('div');
        orderDetails.className = 'order-details';
        
        const orderName = document.createElement('h3');
        orderName.textContent = order.name;
        
        const orderPrice = document.createElement('p');
        orderPrice.textContent = `Price: ${order.price}`;

        const orderWeight = document.createElement('p');
        orderWeight.textContent = `Weight: ${order.weight}`;
        
        const orderRetailer = document.createElement('p');
        orderRetailer.textContent = `Retailer: ${order.retailer}`;
        
        const orderContact = document.createElement('p');
        orderContact.textContent = `Contact: ${order.contact}`;
        
        const orderPhone = document.createElement('p');
        orderPhone.textContent = `Phone: ${order.phone}`;
        
        const orderAddress = document.createElement('p');
        orderAddress.textContent = `Address: ${order.address}`;
        
        const orderDuration = document.createElement('p');
        orderDuration.textContent = `Duration: ${order.duration}`;

        // Create profile icon element
        const profileIcon = document.createElement('a');
        profileIcon.href = `#`; // Replace with the actual URL to the retailer's profile
        profileIcon.className = 'profile-icon';
        profileIcon.innerHTML = '👤'; // Unicode character for a person icon

        orderDetails.append(orderName, orderPrice, orderWeight, orderRetailer, orderContact, orderPhone, orderAddress, orderDuration, profileIcon);
        orderBlock.append(orderImage, orderDetails);
        
        return orderBlock;
    };

    const ongoingOrdersContainer = document.getElementById('ongoing-orders');
    if (ongoingOrdersContainer) {
        ongoingOrders.forEach(order => {
            ongoingOrdersContainer.appendChild(createOrderBlock(order));
        });
    }

    const previousOrdersContainer = document.getElementById('previous-orders');
    if (previousOrdersContainer) {
        previousOrders.forEach(order => {
            previousOrdersContainer.appendChild(createOrderBlock(order));
        });
    }
});